function setNavBar() {
  jQuery(".nav-icon-btn").on("click", function () {

    jQuery(".navbar .nav-icon").toggleClass("nav-icon--active"),
      jQuery(".header__top-row").toggleClass("header__top-row--mobile"),
      jQuery(".navbar").toggleClass("navbar--active"),
      jQuery(".overlay").toggleClass("overlay--active");
  });
}

function setHeaderDropdowns() {
 
  jQuery(".header-bottom__nav li a").click(function (e) {
    
      jQuery(".header-bottom__nav li a").removeClass("active"),
      jQuery(this).addClass("active");
  });
}

function setDropdownListeners() {
  jQuery(".menu-item-has-children").click(function (e) {
    e.stopPropagation(); // Зупиняє подальше спливання події

    // Закриває всі відкриті підменю
    jQuery(".menu-item-has-children.active").not(this).removeClass("active");
    jQuery(".menu-item-has-children .submenu").slideUp(300);

    // Відкриває або закриває поточне підменю
    jQuery(this).toggleClass("active");
    jQuery(this).find(".submenu").slideToggle(300);
  });

  jQuery(document).click(function () {
    // Закриває всі підменю при кліку поза ними
    jQuery(".menu-item-has-children.active").removeClass("active");
    jQuery(".menu-item-has-children .submenu").slideUp(300);
  });

  

  // Зупиняє спливання події при кліку на дочірній елемент підменю
  jQuery(".menu-item-has-children .submenu li").click(function (e) {
    e.stopPropagation();
    jQuery(this).parents(".menu-item-has-children").find("span").text(t(this).text());
    jQuery(this).parents(".menu-item-has-children").find("input").attr("value", t(this).attr("id"));
  });
}

function initHeaderFunctions(){
  setNavBar();
  setHeaderDropdowns();
  setDropdownListeners();
}

jQuery(document).ready(function () {
  initHeaderFunctions();

  jQuery('#menu-item-770 , #menu-item-5699').attr('data-fancybox','');
  jQuery('#menu-item-770 , #menu-item-5699').attr('data-src','#diler-popup');

  jQuery('#menu-item-800').attr('data-fancybox','');
  jQuery('#menu-item-800').attr('data-src','#diler-popup');

  jQuery('#mobile-menu .menu-item-has-children a').on('click',function(e){
    e.preventdefault();
  });
 
});