
"use strict";

jQuery(document).ready(function (e) {
  
  new Swiper(".reviews__swiper", { loop: !0, navigation: { nextEl: ".swiper-button-next", prevEl: ".swiper-button-prev" }, scrollbar: { el: ".swiper-scrollbar" } });
  var i = e(".delivery__timer__hour span"),
      s = e(".delivery__timer__minute span"),
      o = e(".delivery__timer__second span"),
      a = new Date(),
      l =
          (a.setHours(0),
          a.setMinutes(0),
          a.setSeconds(0),
          a.setMilliseconds(0),
          a.setDate(a.getDate() + 1),
          setInterval(function () {
              var e = new Date().getTime(),
                  e = a - e,
                  t = Math.floor((e % 864e5) / 36e5),
                  r = Math.floor((e % 36e5) / 6e4),
                  n = Math.floor((e % 6e4) / 1e3);
              i.text(t < 10 ? "0" + t : t), s.text(r < 10 ? "0" + r : r), o.text(n < 10 ? "0" + n : n), e < 0 && (clearInterval(l), a.setDate(a.getDate() + 1));
          }, 1e3));
});

const images = document.querySelectorAll('.h-popup-image');
const popups = [];

images.forEach(image => {
  const popup = document.createElement('div');
  popup.classList.add('h-popup');

  const imageSrc = image.getAttribute('src');
  const popupImage = document.createElement('img');
  popupImage.setAttribute('src', imageSrc);
  popupImage.classList.add('h-popup-image-zoom');
  popup.appendChild(popupImage);

  const closeBtn = document.createElement('div');
  closeBtn.classList.add('h-popup-close');
  closeBtn.innerHTML = '<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 40 40"><path fill="#fff" d="M27.27 13.637l-3.637-3.637-10.908 10.91-10.91-10.91-3.637 3.637 10.91 10.908-10.91 10.91 3.637 3.637 10.91-10.91 10.908 10.91 3.637-3.637-10.91-10.908z"/></svg>';
  popup.appendChild(closeBtn);

  document.body.appendChild(popup);
  popups.push(popup);

  image.addEventListener('click', () => {
    popup.style.display = 'flex';
  });

  closeBtn.addEventListener('click', () => {
    popup.style.display = 'none';
  });
});

